import React from 'react';
import { useState, useEffect } from 'react'  //used state hook and effect fook
import SubmitItemButton from '../Components/SubmitItemButton'
import Badge from 'react-bootstrap/Badge'
import Card from 'react-bootstrap/Card'

function AddItemForm() {
  const MOCK_API_URL = "https://66038fa12393662c31cf3170.mockapi.io/12345/clothes"
  const [clothes, setClothes] = useState([]);
 //POST
  const [newClothName, setNewClothName] = useState('');
  const [newClothType, setNewClothType] = useState('');
  const [newClothColor, setNewClothColor] = useState('');
  const [newClothSize, setNewClothSize] = useState('');
  const [newClothBrand, setNewClothBrand] = useState('');
  const [newClothImage, setNewClothImage] = useState('');

 useEffect(() => {
    getClothes();
  }, []);

  function getClothes() {
    fetch(MOCK_API_URL)
    .then(data => data.json())
    .then(data => setClothes(data))
  }

  function postNewCloth(e){
    e.preventDefault();

    fetch(MOCK_API_URL, {
      method: 'POST',
      headers: {"Content-Type": "application/json"},
      body: JSON.stringify({
        name: newClothName,
        type: newClothType,
        color: newClothColor,
        size: newClothSize,
        brand: newClothBrand,
        image: newClothImage
      })
    }).then(() => {
      getClothes();
      setNewClothName('');
      setNewClothType('');
      setNewClothSize('');
      setNewClothColor('');
      setNewClothBrand('');
      setNewClothImage('');
    })
  }

  return (
    <div className="AddItemForm">
      <Card className="card--additemform">
        <form onSubmit={(e) => postNewCloth(e)}>  
          <Card.Title><Badge bg="warning">New</Badge> Item Form</Card.Title>
          <label>Cloth Name:</label>
          <input value={newName} onChange={(e) => setNewClothName(e.target.value)}></input><br></br>

          <label>Cloth Type:</label>
          <input value={newType} onChange={(e) => setNewClothType(e.target.value)}></input><br></br>

          <label>Cloth Color:</label>
          <input value={newColor} onChange={(e) => setNewClothColor(e.target.value)}></input><br></br>

          <label>Cloth Size:</label>
          <input value={newSize} onChange={(e) => setNewClothSize(e.target.value)}></input><br></br>

          <label>Cloth Brand:</label>
          <input value={newBrand} onChange={(e) => setNewClothBrand(e.target.value)}></input><br></br>

          <label>Cloth Image:</label>
          <input value={newImage} onChange={(e) => setNewClothImage(e.target.value)}></input><br></br>

          <button onClick={(e) => postNewCloth(e)} className="sbm--btn"><SubmitItemButton /></button>
        </form>
      </Card>
    </div>
  );
} 

export default AddItemForm;
