import React from 'react';
import AddItemForm from './AddItemForm';

const AddItem = ({ history, clothes, setClothes }) => {
  const handleOnSubmit = (cloth) => {
    setClothes([cloth, ...clothes]);
    history.push('/');
  };

  return (
    <React.Fragment>
      <AddItemForm handleOnSubmit={handleOnSubmit} />
    </React.Fragment>
  );
};

export default AddItem;









